liquid.css:
.image-slider-section {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  overflow: hidden;
}

*,
*::after,
*::before {
  box-sizing: border-box;
}

.before-after-container {
  display: grid;
  place-content: center;
  position: relative;
  overflow: hidden;
  --position: 50%;
}

.image-container {
  max-width: {{slider_max_width}}px;
  max-height: 90vh;
  aspect-ratio: 3/2;
}

.slider-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
  display: block;
}

.image-before {
  position: absolute;
  inset: 0;
  width: var(--position);
}

.slider {
  position: absolute;
  inset: 0;
  cursor: pointer;
  opacity: 0;
  width: 100%;
  height: 100%;
  z-index: 2;
}

.slider:focus-visible ~ .slider-button {
  outline: 3px solid {{handle_button_border_color}};
  outline-offset: 3px;
}

.slider-line {
  position: absolute;
  inset: 0;
  width: {{handle_width}}px;
  height: 100%;
  background-color: {{handle_color}};
  left: var(--position);
  transform: translateX(-50%);
  pointer-events: none;
  box-shadow: 0 0 0 1px rgba(0,0,0,0.1);
}

.slider-button {
  position: absolute;
  background-color: {{handle_button_color}};
  color: {{handle_icon_color}};
  padding: {{handle_button_padding}}px;
  border-radius: 50%;
  display: grid;
  place-items: center;
  top: 50%;
  left: var(--position);
  transform: translate(-50%, -50%);
  pointer-events: none;
  box-shadow: {{handle_shadow}};
  border: {{handle_button_border_width}}px solid {{handle_button_border_color}};
  transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.before-after-container:hover .slider-button {
  transform: translate(-50%, -50%) scale(1.1);
  box-shadow: 0 8px 25px rgba(0,0,0,0.2);
}

.slider-title {
  animation: fadeInUp 0.6s ease 0.2s both;
}

.slider-description {
  animation: fadeInUp 0.6s ease 0.4s both;
}

.before-after-container {
  animation: fadeInUp 0.8s ease 0.6s both;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Mobile Responsive */
@media (max-width: 768px) {
  .image-slider-section {
    padding: 40px 15px !important;
  }

  .slider-title {
    font-size: calc({{title_font_size}}rem * 0.8) !important;
  }

  .slider-description {
    font-size: calc({{description_font_size}}rem * 0.9) !important;
  }

  .image-container {
    aspect-ratio: 1/1;
  }

  .slider-button {
    padding: calc({{handle_button_padding}}px * 0.8) !important;
  }

  .slider-line {
    width: 3px !important;
  }
}

@media (max-width: 480px) {
  .slider-title {
    font-size: calc({{title_font_size}}rem * 0.7) !important;
  }

  .image-container {
    max-height: 70vh;
  }
}

liquid.javascript:
document.addEventListener('DOMContentLoaded', function() {
  const sliderSection = document.querySelector('.image-slider-section');

  if (sliderSection) {
    console.log('âœ… Image Slider initialized');

    const container = sliderSection.querySelector('.before-after-container');
    const slider = sliderSection.querySelector('.slider');

    if (container && slider) {
      // Main slider functionality
      slider.addEventListener('input', function(e) {
        container.style.setProperty('--position', `${e.target.value}%`);
        console.log('ðŸ”„ Slider position:', e.target.value + '%');
      });

      // Auto-demo animation on load
      setTimeout(function() {
        let position = 50;
        let direction = 1;
        let animationCount = 0;

        const demoAnimation = setInterval(function() {
          position += direction * 2;

          if (position >= 80 || position <= 20) {
            direction *= -1;
            animationCount++;
          }

          container.style.setProperty('--position', position + '%');
          slider.value = position;

          if (animationCount >= 2) {
            clearInterval(demoAnimation);
            // Return to center
            container.style.setProperty('--position', '50%');
            slider.value = 50;
            console.log('ðŸŽ­ Demo animation completed');
          }
        }, 50);
      }, 1000);

      // Add keyboard support enhancement
      slider.addEventListener('keydown', function(e) {
        const step = e.shiftKey ? 10 : 1;
        const currentValue = parseInt(this.value);

        if (e.key === 'ArrowLeft') {
          const newValue = Math.max(0, currentValue - step);
          this.value = newValue;
          container.style.setProperty('--position', newValue + '%');
          e.preventDefault();
        } else if (e.key === 'ArrowRight') {
          const newValue = Math.min(100, currentValue + step);
          this.value = newValue;
          container.style.setProperty('--position', newValue + '%');
          e.preventDefault();
        }
      });

      // Add smooth transition on slider release
      slider.addEventListener('mouseup', function() {
        container.style.transition = '--position 0.2s ease';
        setTimeout(() => {
          container.style.transition = '';
        }, 200);
      });

      // Touch support for mobile
      slider.addEventListener('touchend', function() {
        container.style.transition = '--position 0.2s ease';
        setTimeout(() => {
          container.style.transition = '';
        }, 200);
      });
    }
  }
});
